mapper.select:
select count(*) as r1,substr(changTime,6,10) as changTime
from sj_task_detail where taskId=#{taskId} and r2=#{taskType}
group by substr(changTime,1,15) order by  substr(changTime,6,10) desc

select count(*) as r1,substr(changTime,6,8) as changTime
from sj_task_detail where taskId=#{taskId} and r2=#{taskType}
group by substr(changTime,1,13) desc

select

from sj_task_detail
where taskId = #{id,jdbcType=INTEGER}

  and r2 = 1


    and r3= #{gjcId,jdbcType=INTEGER}

order by changTime desc

select count(*)  from sj_task_detail
where 0=0

 and bundleId=#{bId} and ip= #{ip}


  and taskId=#{taskId} and idfa=#{idfa}
